dict.string:
if [[ -z $TM_FILENAME ]]
  then title="Textile Preview"
  else title="${TM_FILENAME%.*}"
fi

TEMPFILE=`mktemp "/tmp/textile_XXXXXX"`
{
. "$TM_SUPPORT_PATH/lib/html.sh"
htmlHeader "$title" '<style type="text/css">
	body {
		background-color: #eee;
	}
	.contents {	
		background: white;
		font-family: Verdena, sans-serif;
		font-size: 13px;
		border: 1px #888 solid;
		padding: 0 1em;
	}
	</style>'
brginTag div 'contents'
	"$TM_SUPPORT_PATH/bin/Textile.rb"
endTag
htmlFooter; } >"$TEMPFILE.html"
rm "$TEMPFILE"
open -a Safari "$TEMPFILE.html"

osascript -e 'activate application "Safari.app"' -e 'tell application "System Events"' -e ' tell process "Safari"' -e ' keystroke "i" using {command down}' -e ' end tell' -e 'end tell' -e 'tell the application "Safari" to close window 1'
